import './App.css';
import Graph from './components/Graph';
import Forms from './components/Forms';
import { Doughnut } from 'react-chartjs-2';

function App() {
  const config = {
    data: {
        datasets: [{
            data: [300, 50, 100],
            backgroundColor: [
              'rgb(255, 99, 132)',
              'rgb(54, 162, 235)',
              'rgb(255, 205, 86)'
            ],  
            hoverOffset: 4
          }]
        },  
        options:{
            cutout: 115
        }
}

  return (
    <div className="App">
      <div className = "container mx-auto max-w-6xl text-center drop-shadow-lg text-gray-800">
        <h1 className='text-4xl py-8 mb-10 bg-slate-800 text-white rounded'>Transactions</h1>
      </div>

      {/*grid columns*/}

    <div className = "grid md:grid-cols-2 gap-4">
      {/*chart*/}
      <Graph>
      <Doughnut {...config}></Doughnut> 
      </Graph>
      {/*form*/}
      <Forms>

      </Forms>
    </div>


    </div>



  );
}

export default App;
